#include "camera.h"
#include <QDebug>
#include <QDir>
#include <QFile>
#include <QPixMap>
#include <QNetworkRequest>
#include <QUrlQuery>
#include <QNetworkAccessManager>
#include <QVariantMap>
#include <QJsonDocument>
#include <QBuffer>
Camera::Camera(QWidget *parent)
    : QWidget(parent)
{

}

void Camera::init(int width, int height){
    layout = new QVBoxLayout(this);
    this->setMinimumHeight(height);
    this->setMinimumWidth(width);

    QList<QByteArray> liste = QCamera::availableDevices();


    _camera = new QCamera(liste[0],this);
    _cameraViewer = new QCameraViewfinder();
    _cameraViewer->setMinimumHeight(height-50);
    _cameraViewer->setMinimumWidth(width-20);
    _camera->start();
    _camera->setViewfinder(_cameraViewer);

    _imageCapture = new QCameraImageCapture(_camera);

    _labelTimer = new QLabel("5.0");
    _labelTimer->setMaximumHeight(50);
    _labelTimer->setAlignment(Qt::AlignCenter);
    _labelTimer->setStyleSheet("QLabel{font-size:40px;color:red;}");

    layout->addWidget(_labelTimer);
    layout->addWidget(_cameraViewer);

    _timer = new QTimer(this);

    connect(_timer, &QTimer::timeout, this, &Camera::updateTimer);
    _timer->start(100);


    int rand = randInt(0,50);

    _dRand = double(rand) / 10;



    _qLabelImage = new QLabel(this);
    _qLabelImage->setStyleSheet("QLabel {border-radius:40px}");

    _qLabelImage->setMinimumWidth(this->width());//50 pou rle label
    _qLabelImage->setMinimumHeight(this->height());

}

void Camera::updateTimer(){

    double timerActuel = _labelTimer->text().toDouble();
    qDebug() << _dRand;
    if(timerActuel == _dRand)
    {
        qDebug() << "OUI";
        _imageCapture->capture(QDir::temp().path()+"/"+QString::number(timerActuel));
    }

    timerActuel -= 0.1;

    if(timerActuel >=0){

        _labelTimer->setText(QString::number(timerActuel));
    }
    if(timerActuel == 0){
        _camera->stop();

        // set a scaled pixmap to a w x h window keeping its aspect ratio
        QPixmap p(QDir::temp().path()+"/"+QString::number(_dRand)+".jpg");
        //QPixmap p = QPixmap();
        //qDebug() << p.loadFromData("");

        int w = _qLabelImage->width();
        int h = _qLabelImage->height();
        _qLabelImage->setPixmap(p.scaled(w,h,Qt::KeepAspectRatio));
        _cameraViewer->hide();
        disconnect(_timer, &QTimer::timeout, this, &Camera::updateTimer);
        QFile pic(QDir::temp().path()+"/"+QString::number(_dRand)+".jpg");
        pic.open(QFile::ReadWrite);


        QVariantMap top;
        top.insert( "privacy", QString( "1" ) );
        top.insert( "sendNotification", QString( "03/03/2017 14:07:00" ) );

        top.insert( "photo", QString(pic.readAll().toBase64()) + "" );
        qDebug() << top.find("photo").value().toString();
        const QJsonDocument doc = QJsonDocument::fromVariant(top);
        QByteArray postData = doc.toJson();

        QNetworkAccessManager* manager = new QNetworkAccessManager(this);

        QNetworkRequest req;
        req.setUrl(QUrl("http://localhost:8080/pic"));
        req.setHeader(QNetworkRequest::ContentTypeHeader,"application/json");

        QNetworkReply *reply = manager->post(req,postData);


        pic.remove();//Une fois envoyé on la supprime du dossier temp
    }


}



int Camera::randInt(int low, int high)
{
    return qrand() % ((high + 1) - low) + low;
}


